mapper.select:
select ${fieldName} as value,time
from 
	${tableName}
where
	time>=#{beginTime}
order by time

		SELECT ${fieldName} as value,time
		FROM
		    ${tableName}
		WHERE
          id=(SELECT MAX(id) FROM ${tableName});

      select * from (
        select ${fieldName} as value,time
		    from
			    ${tableName}
		    order by time desc limit ${offset}
          ) as a ORDER by a.time ASC;

          select  ${fieldName} as value,time
		    from
			    ${tableName}
			WHERE  DATE(time)=#{date} AND time%${timestamp}=0;

select ${fieldName} as value,time
from
	${tableName}
where
	time>=#{beginTime}
order by time limit ${pageNumber},${pageSize}
